adaptiveCard.cardData:
{
  "$schema": "http://adaptivecards.io/schemas/adaptive-card.json",
  "type": "AdaptiveCard",
  "version": "1.4",
  "body": [
    {
      "type": "TextBlock",
      "text": "Welcome to Plant Pals",
      "weight": "bolder",
      "size": "large"
    },
    {
      "type": "TextBlock",
      "text": "A dating app for introverted houseplants",
      "wrap": true
    },
    {
      "type": "Image",
      "url": "https://example.com/plant-pals-logo.jpg",
      "size": "medium",
      "altText": "Plant Pals Logo"
    },
    {
      "type": "FactSet",
      "facts": [
        {
          "title": "Profile Creation:",
          "value": "Select plant type, light preferences, watering needs, age, and size."
        },
        {
          "title": "Matching Algorithm:",
          "value": "Match based on light and watering needs."
        },
        {
          "title": "Interaction:",
          "value": "Like/Dislike, Messaging, Virtual Garden."
        },
        {
          "title": "Community Features:",
          "value": "Forums, Events."
        },
        {
          "title": "Notifications:",
          "value": "Reminders for care, alerts for matches."
        }
      ]
    },
    {
      "type": "TextBlock",
      "text": "Join us and let your plants find their perfect match!",
      "wrap": true
    }
  ],
  "actions": [
    {
      "type": "Action.OpenUrl",
      "title": "Get Started",
      "url": "https://example.com/get-started"
    }
  ]
}
